    /**
     * Install the MUEternizerModule application.
     *
     * @return boolean True on success, or false
     *
     * @throws RuntimeException Thrown if database tables can not be created or another error occurs
     */
    public function install()
    {
        $logger = $this->container->get('logger');
    
        // create all tables from according entity definitions
        try {
            $this->schemaTool->create($this->listEntityClasses());
        } catch (\Exception $e) {
            $this->addFlash('error', $this->__('Doctrine Exception') . ': ' . $e->getMessage());
            $logger->error('{app}: Could not create the database tables during installation. Error details: {errorMessage}.', ['app' => 'MUEternizerModule', 'errorMessage' => $e->getMessage()]);
    
            return false;
        }
    
        // set up all our vars with initial values
        $this->setVar('mail', '');
        $this->setVar('order',  'descending' );
        $this->setVar('moderate',  'guests' );
        $this->setVar('formposition',  'below' );
        $this->setVar('ipsave', false);
        $this->setVar('editentries', false);
        $this->setVar('period', 0);
        $this->setVar('simplecaptcha', false);
        $this->setVar('moderationGroupForEntries', 2);
        $this->setVar('entryEntriesPerPage', 10);
    
        // create the default data
        $this->createDefaultData();
    
        // install subscriber hooks
        $this->hookApi->installSubscriberHooks($this->bundle->getMetaData());
        
    
        // initialisation successful
        return true;
    }
    
