namespace MU\EternizerModule\Container\Base;

use ModUtil;
use ServiceUtil;
use Symfony\Component\Routing\RouterInterface;
use Zikula\Common\Translator\Translator;
use Zikula\Core\LinkContainer\LinkContainerInterface;

/**
 * This is the link container service implementation class.
 */
class LinkContainer implements LinkContainerInterface
{
    /**
     * @var Translator
     */
    protected $translator;

    /**
     * @var RouterInterface
     */
    protected $router;

    /**
     * Constructor.
     * Initialises member vars.
     *
     * @param Translator      $translator Translator service instance.
     * @param Routerinterface $router     The router service.
     */
    public function __construct($translator, RouterInterface $router)
    {
        $this->translator = $translator;
        $this->router = $router;
    }

    /**
     * Returns available header links.
     *
     * @return array Array of header links.
     */
    public function getLinks($type = LinkContainerInterface::TYPE_ADMIN)
    {
        $links = array();
        $serviceManager = ServiceUtil::getManager();
        $request = $serviceManager->get('request');

        $controllerHelper = $serviceManager->get('mueternizermodule.controller_helper');
        $utilArgs = array('api' => 'ajax', 'action' => 'getLinks');
        $allowedObjectTypes = $controllerHelper->getObjectTypes('api', $utilArgs);

        $permLevel = LinkContainerInterface::TYPE_ADMIN == $type ? ACCESS_ADMIN : ACCESS_READ;

        $permissionHelper = $serviceManager->get('zikula_permissions_module.api.permission');

        
        if ('admin' == $type) {
            if ($permissionHelper->hasPermission($this->getBundleName() . '::', '::', ACCESS_READ)) {
                $links[] = array('url' => $this->router->generate('mueternizermodule_user_index'),
                                 'text' => $this->translator->__('Frontend'),
                                 'title' => $this->translator->__('Switch to user area.'),
                                 'icon' => 'home');
            }
            
            if (in_array('entry', $allowedObjectTypes)
                && $permissionHelper->hasPermission($this->getBundleName() . ':Entry:', '::', $permLevel)) {
                $links[] = array('url' => $this->router->generate('mueternizermodule_entry_adminview'),
                                 'text' => $this->translator->__('Entries'),
                                 'title' => $this->translator->__('Entry list'));
            }
            if ($permissionHelper->hasPermission($this->getBundleName() . '::', '::', ACCESS_ADMIN)) {
                $links[] = array('url' => $this->router->generate('mueternizermodule_admin_config'),
                                 'text' => $this->translator->__('Configuration'),
                                 'title' => $this->translator->__('Manage settings for this application'),
                                 'icon' => 'wrench');
            }
        }
        if ('user' == $type) {
            if ($permissionHelper->hasPermission($this->getBundleName() . '::', '::', ACCESS_ADMIN)) {
                $links[] = array('url' => $this->router->generate('mueternizermodule_admin_index'),
                                 'text' => $this->translator->__('Backend'),
                                 'title' => $this->translator->__('Switch to administration area.'),
                                 'icon' => 'wrench');
            }
            
            if (in_array('entry', $allowedObjectTypes)
                && $permissionHelper->hasPermission($this->getBundleName() . ':Entry:', '::', $permLevel)) {
                $links[] = array('url' => $this->router->generate('mueternizermodule_entry_view'),
                                 'text' => $this->translator->__('Entries'),
                                 'title' => $this->translator->__('Entry list'));
            }
        }

        return $links;
    }

    public function getBundleName()
    {
        return 'MUEternizerModule';
    }
}
