/**
 * Version information base class.
 */
class Eternizer_Base_Version extends Zikula_AbstractVersion
{
    public function getMetaData()
    {
        $meta = array();
        // the current module version
        $meta['version']              = '1.1.2';
        // the displayed name of the module
        $meta['displayname']          = $this->__('Eternizer');
        // the module description
        $meta['description']          = $this->__('Eternizer module generated by ModuleStudio 0.5.4.');
        //! url version of name, should be in lowercase without space
        $meta['url']                  = $this->__('eternizer');
        // core requirement
        $meta['core_min']             = '1.3.1'; // requires minimum 1.3.1 or later
        $meta['core_max']             = '1.3.99'; // not ready for 1.4.0 yet

        // define special capabilities of this module
        $meta['capabilities'] = array(
                          HookUtil::SUBSCRIBER_CAPABLE => array('enabled' => true)
/*,
                          HookUtil::PROVIDER_CAPABLE => array('enabled' => true), // TODO: see #15
                          'authentication' => array('version' => '1.0'),
                          'profile'        => array('version' => '1.0', 'anotherkey' => 'anothervalue'),
                          'message'        => array('version' => '1.0', 'anotherkey' => 'anothervalue')
*/
        );

        // permission schema
        // DEBUG: permission schema aspect starts
        $meta['securityschema'] = array(
            'Eternizer::' => '::',

            'Eternizer:Entry:' => 'EntryID::'
        );
        // DEBUG: permission schema aspect ends



        return $meta;
    }

    /**
     * Define hook subscriber bundles.
     */
    protected function setupHookBundles()
    {

        $bundle = new Zikula_HookManager_SubscriberBundle($this->name, 'subscriber.eternizer.ui_hooks.entries', 'ui_hooks', __('eternizer Entries Display Hooks'));
        // Display hook for view/display templates.
        $bundle->addEvent('display_view', 'eternizer.ui_hooks.entries.display_view');
        // Display hook for create/edit forms.
        $bundle->addEvent('form_edit', 'eternizer.ui_hooks.entries.form_edit');
        // Display hook for delete dialogues.
        $bundle->addEvent('form_delete', 'eternizer.ui_hooks.entries.form_delete');
        // Validate input from an ui create/edit form.
        $bundle->addEvent('validate_edit', 'eternizer.ui_hooks.entries.validate_edit');
        // Validate input from an ui create/edit form (generally not used).
        $bundle->addEvent('validate_delete', 'eternizer.ui_hooks.entries.validate_delete');
        // Perform the final update actions for a ui create/edit form.
        $bundle->addEvent('process_edit', 'eternizer.ui_hooks.entries.process_edit');
        // Perform the final delete actions for a ui form.
        $bundle->addEvent('process_delete', 'eternizer.ui_hooks.entries.process_delete');
        $this->registerHookSubscriberBundle($bundle);

        $bundle = new Zikula_HookManager_SubscriberBundle($this->name, 'subscriber.eternizer.filter_hooks.entries', 'filter_hooks', __('eternizer Entries Filter Hooks'));
        // A filter applied to the given area.
        $bundle->addEvent('filter', 'eternizer.filter_hooks.entries.filter');
        $this->registerHookSubscriberBundle($bundle);


    }
}

