namespace MU\EternizerModule\Event\Base;

use Symfony\Component\EventDispatcher\Event;
use MU\EternizerModule\Entity\EntryEntity;

/**
 * Event base class for filtering entry processing.
 */
class FilterEntryEvent extends Event
{
    /**
     * @var EntryEntity Reference to treated entity instance.
     */
    protected $entry;

    public function __construct(EntryEntity $entry)
    {
        $this->entry = $entry;
    }

    public function getEntry()
    {
        return $this->entry;
    }
}
