namespace MU\EternizerModule\Event\Base;

use Symfony\Component\EventDispatcher\Event;
use MU\EternizerModule\Entity\EntryEntity;

/**
 * Event base class for filtering entry processing.
 */
class AbstractFilterEntryEvent extends Event
{
    /**
     * @var EntryEntity Reference to treated entity instance.
     */
    protected $entry;

    /**
     * @var array Entity change set for preUpdate events.
     */
    protected $entityChangeSet = [];

    /**
     * FilterEntryEvent constructor.
     *
     * @param EntryEntity $entry Processed entity
     * @param array $entityChangeSet Change set for preUpdate events
     */
    public function __construct(EntryEntity $entry, $entityChangeSet = [])
    {
        $this->entry = $entry;
        $this->entityChangeSet = $entityChangeSet;
    }

    /**
     * Returns the entity.
     *
     * @return EntryEntity
     */
    public function getEntry()
    {
        return $this->entry;
    }

    /**
     * Returns the change set.
     *
     * @return array
     */
    public function getEntityChangeSet()
    {
        return $this->entityChangeSet;
    }
}
