                ],'choices' => [
                    $this->__('Nothing') => 'nothing',
                    $this->__('Guests') => 'guests',
                    $this->__('All') => 'all'
                ],
                'choices_as_values' => true,
                'multiple' => false
            ])
            ->add('formposition', ChoiceType::class, [
                'label' => $this->__('Formposition') . ':',
                'data' => isset($this->moduleVars['formposition']) ? $this->moduleVars['formposition'] : '',
                'empty_data' => '',
                'attr' => [
