/**
 * Replaces a given needle id by the corresponding content.
 *
 * @param array $args Arguments array
 *     int nid The needle id
 *
 * @return string Replaced value for the needle
 */
function MUEternizerModule_needleapi_entry_base($args)
{
    // Get arguments from argument array
    $nid = $args['nid'];
    unset($args);

    // cache the results
    static $cache;
    if (!isset($cache)) {
        $cache = array();
    }

    $dom = \ZLanguage::getModuleDomain('MUEternizerModule');

    if (empty($nid)) {
        return '<em>' . \DataUtil::formatForDisplay(__('No correct needle id given.', $dom)) . '</em>';
    }

    if (isset($cache[$nid])) {
        // needle is already in cache array
        return $cache[$nid];
    }

    if (!\ModUtil::available('MUEternizerModule')) {
        $cache[$nid] = '<em>' . \DataUtil::formatForDisplay(__f('Module %s is not available.', array('MUEternizerModule'), $dom)) . '</em>';

        return $cache[$nid];
    }

    // strip application prefix from needle
    $needleId = str_replace('ETERNIZER', '', $nid);

    $router = \ServiceUtil::getService('router');

    if ($needleId == 'ENTRIES') {
        if (!\SecurityUtil::checkPermission('MUEternizerModule:Entry:', '::', ACCESS_READ)) {
            $cache[$nid] = '';

            return $cache[$nid];
        }
    }

    $cache[$nid] = '<a href="' . $router->generate('mueternizermodule_entries_view') . '" title="' . __('View entries', $dom) . '">' . __('Entries', $dom) . '</a>';
    $needleParts = explode('-', $needleId);
    if ($needleParts[0] != 'ENTRY' || count($needleParts) < 2) {
        $cache[$nid] = '';

        return $cache[$nid];
    }

    $permissionApi = \ServiceUtil::get('zikula_permissions_module.api.permission');
    $entityId = (int)$needleParts[1];

    if (!$permissionApi->hasPermission('MUEternizerModule:Entry:', $entityId . '::', ACCESS_READ)) {
        $cache[$nid] = '';

        return $cache[$nid];
    }

    $selectionHelper = \ServiceUtil::get('mu_eternizer_module.selection_helper');
    $entity = $selectionHelper->getEntity('entry', $entityId);
    if (null === $entity) {
        $cache[$nid] = '<em>' . __f('Entry with id %s could not be found', [$entityId], $dom) . '</em>';

        return $cache[$nid];
    }

    $title = $entity->getTitleFromDisplayPattern();

    $cache[$nid] = '<a href="' . $router->generate('mueternizermodule_entries_display', ['id' => $entityId]) . '" title="' . str_replace('"', '', $title) . '">' . $title . '</a>';

    return $cache[$nid];
}
